INSERT INTO ShipModel(Name,Description,Price,Availability,ImagePath,ShipType) VALUES ('Targa 27.1','Targa 27.1 - элегантный финский катер длиной 8,4 метра, сочетающий в себе высокую производительность и роскошный дизайн. Корпус из стекловолокна обеспечивает прочность и плавность хода, а мощные подвесные двигатели Yamaha позволяют развивать скорость до 45 узлов. Ширина 3.04м Осадка 1.05м. 2014 года выпуска, флайбридж одни руки. Сухой вес 3,9 тонны.',135000,'Модель в наличии','C:\Users\леха\Desktop\2 курс\4sem\лабы\ооп\lab4\lab4\pics\boatpics\targa271.png','Финский катер');
