INSERT INTO ShipModel(Name,Description,Price,Availability,ImagePath,ShipType) VALUES ('Beneteau Flyer 7.7 SPORTdeck','Beneteau Flyer 7.7 SPORTdeck представляет собой современный спортивно-развлекательный катер от известного французского бренда Beneteau. Эта модель сочетает в себе динамичные ходовые качества и комфортабельное пространство для семейного отдыха.
